resources.string:
query {
  viewer {
    name
    email
    login
    avatarUrl
    followers(first: 10) {
      totalCount
    }
    following(first: 10) {
      totalCount
    }
    pinnedItems(first: 3) {
       edges {
        cursor
      }
    }
    repositories(first: 10) {
      edges {
        repository:node {
          name
          description
          forkCount
          primaryLanguage {
            name
          }
        }
      }
    }
    starredRepositories(first: 10) {
      edges {
        repository:node {
          name
          description
          forkCount
          primaryLanguage {
            name
          }
        }
      }
    }
  }
}
